import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { ThemeContext } from '../../contexts/ThemeContext';
import { HiArrowRight } from "react-icons/hi";
import { servicesData } from '../../data/servicesData';
import { makeStyles } from '@material-ui/core/styles';

import './Services.css';
import SingleService from './SingleService/SingleService';

function Services({ showLimited = true }) { // Added showLimited prop with default value true

    const { theme } = useContext(ThemeContext);

    const useStyles = makeStyles(() => ({
        viewAllBtn: {
            color: theme.tertiary,
            backgroundColor: theme.primary,
            transition: 'color 0.2s',
            "&:hover": {
                color: theme.secondary,
                backgroundColor: theme.primary,
            }
        },
        viewArr: {
            color: theme.tertiary,
            backgroundColor: theme.secondary70,
            width: '40px',
            height: '40px',
            padding: '0.5rem',
            fontSize: '1.05rem',
            borderRadius: '50%',
            cursor: 'pointer',
            transition: 'background-color 0.2s',
            "&:hover": {
                color: theme.tertiary,
                backgroundColor: theme.secondary,
            }
        },
    }));

    const classes = useStyles();
    const limitedServices = showLimited ? servicesData.slice(0, 3) : servicesData; // Determine the number of services to show

    return (
        <>
            {limitedServices.length > 0 && (
                <div className="services" id="services" style={{ backgroundColor: theme.secondary }}>
                    <div className="services-header">
                        <h1 style={{ color: theme.primary }}>Leadership Roles</h1>
                    </div>
                    <div className="services-body">
                        <h2 style={{ color: theme.primary, marginBottom: '1rem' }}>
                            {showLimited ? 'Present Roles' : 'All Roles'}
                        </h2>
                        <div className="services-bodycontainer">
                            {limitedServices.map(service => (
                                <SingleService
                                    key={service.id}
                                    id={service.id}
                                    title={service.title}
                                    date={service.date}
                                    organization={service.organization}
                                    description={service.desc}
                                    icon={service.icon} />
                            ))}
                        </div>
                    </div>
                    {showLimited && (
                        <div className="achievements--viewAll">
                            <Link to="/leaderships">
                                <button className={classes.viewAllBtn}>
                                    View All
                                    <HiArrowRight className={classes.viewArr} />
                                </button>
                            </Link>
                        </div>
                    )}
                </div>
            )}
        </>
    )
}

export default Services;
