import React,{useContext} from 'react';
import { Link } from 'react-router-dom'
import { ThemeContext } from '../../contexts/ThemeContext';
import { HiArrowRight } from "react-icons/hi";
import { servicesData } from '../../data/servicesData';
import { makeStyles } from '@material-ui/core/styles';

import './Services.css'
import SingleService from './SingleService/SingleService';
import { useState} from 'react';


function Services() {

    const { theme } = useContext(ThemeContext);

    const useStyles = makeStyles(() => ({
        viewAllBtn : {
            color: theme.tertiary, 
            backgroundColor: theme.primary,
            transition: 'color 0.2s',
            "&:hover": {
                color: theme.secondary, 
                backgroundColor: theme.primary,
            }
        },
        viewArr : {
            color: theme.tertiary, 
            backgroundColor: theme.secondary70,
            width: '40px',
            height: '40px',
            padding: '0.5rem',
            fontSize: '1.05rem',
            borderRadius: '50%',
            cursor: 'pointer',
            transition: 'background-color 0.2s',
            "&:hover": {
                color: theme.tertiary, 
                backgroundColor: theme.secondary,
            }
        },
    }));

    const classes = useStyles();
     // State to track if the View All button has been clicked
     const [viewAllClicked, setViewAllClicked] = useState(false);

     const handleViewAllClick = () => {
         setViewAllClicked(true);
     }

     return (
         <>
             {servicesData.length > 0 && (
                 <div className="services" id="services" style={{backgroundColor:theme.secondary}}>
                     {/* ... existing code ... */}

                     <div className="achievements--viewAll">
                         {!viewAllClicked && (
                             <Link to="/leaderships" onClick={handleViewAllClick}>
                                 <button className={classes.viewAllBtn}>
                                     View All
                                     <HiArrowRight className={classes.viewArr} />
                                 </button>
                             </Link>
                         )}
                     </div>

                     {viewAllClicked && (
                         <>
                             <h2 style={{ color: theme.primary, marginBottom: '1rem' }}>Present Roles</h2>
                             {/* ... Present Roles content ... */}

                             <h2 style={{ color: theme.primary, marginTop: '2rem', marginBottom: '1rem' }}>Past Roles</h2>
                             {/* ... Past Roles content ... */}
                         </>
                     )}
                 </div>
             )}
         </>
     )
 }

 export default Services;
