import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { ThemeContext } from '../../contexts/ThemeContext';
import { HiArrowRight } from "react-icons/hi";
import { servicesData } from '../../data/servicesData';
import { makeStyles } from '@material-ui/core/styles';

import './Services.css';
import SingleService from './SingleService/SingleService';

function Services({ showLimited = true }) {

    const { theme } = useContext(ThemeContext);

    const useStyles = makeStyles(() => ({
        // ... styles remain unchanged
    }));

    const classes = useStyles();

    // Splitting data into present and past roles
    const presentRoles = showLimited ? servicesData.slice(0, 3) : servicesData.slice(0, 4);
    const pastRoles = showLimited ? [] : servicesData.slice(4);

    return (
        <>
            <div className="services" id="services" style={{ backgroundColor: theme.secondary }}>
                <div className="services-header">
                    <h1 style={{ color: theme.primary }}>Leadership Roles</h1>
                </div>
                <div className="services-body">
                    <h2 style={{ color: theme.primary, marginBottom: '1rem' }}>Present Roles</h2>
                    <div className="services-bodycontainer">
                        {presentRoles.map(service => (
                            <SingleService
                                key={service.id}
                                id={service.id}
                                title={service.title}
                                date={service.date}
                                organization={service.organization}
                                description={service.desc}
                                icon={service.icon} />
                        ))}
                    </div>
                    {pastRoles.length > 0 && (
                        <>
                            <h2 style={{ color: theme.primary, marginTop: '2rem', marginBottom: '1rem' }}>Past Roles</h2>
                            <div className="services-bodycontainer">
                                {pastRoles.map(service => (
                                    <SingleService
                                        key={service.id}
                                        id={service.id}
                                        title={service.title}
                                        date={service.date}
                                        organization={service.organization}
                                        description={service.desc}
                                        icon={service.icon} />
                                ))}
                            </div>
                        </>
                    )}
                </div>
                {showLimited && (
                    <div className="achievements--viewAll">
                        <Link to="/leaderships">
                            <button className={classes.viewAllBtn}>
                                View All
                                <HiArrowRight className={classes.viewArr} />
                            </button>
                        </Link>
                    </div>
                )}
            </div>
        </>
    )
}

export default Services;
